INSERT INTO examples VALUES(1,1,'Thag 1.111','Jentattheragāthā','Dhammo gambhīro duradhigamā bhogā','the Dhamma is profound, wealth is hard to obtain',NULL,'2021-06-09 15:34:25',NULL);
